import React, { useEffect, useMemo, useState } from 'react'
import { Button, Modal } from 'react-bootstrap';
import { Controller, useForm } from 'react-hook-form';
import { editBaggage } from '../../../http/baggageApi';
import { getFlights } from '../../../http/flightApi';
import { getPassengers } from '../../../http/passengerApi';
import { IBaggage } from '../../../interfaces/IBaggage';
import { IFlight } from '../../../interfaces/IFlight';
import { IPassenger } from '../../../interfaces/IPassenger';
import { ISelect } from '../../../interfaces/ISelect';
import { IBaggageCreateData } from './BaggageCreateModal';

interface IProps {
    show: boolean,
    onHide: () => void,
    fetch: () => void,
    item?: IBaggage,
}
  
  export interface IBaggageEditData extends IBaggageCreateData {
    baggageId: number,
  }

export const BaggageEditModal = ({ show, onHide, item, fetch }: IProps) => {
    const {
        control,
        handleSubmit,
        reset,
        formState: { errors },
      } = useForm<IBaggageEditData>();
      const [passengers, setPassengers] = useState<IPassenger[]>([]);
      const [flights, setFlights] = useState<IFlight[]>([]);
    
      useEffect(() => {
        if (item) {
          reset({
            ...item
          });
        }
      }, [item, reset]);
          
      const onSubmit = async (data: IBaggageEditData) => {
        await editBaggage(data.baggageId, data)
          .then(() => {
            onHide();
            fetch();
          })
          .catch(() => alert("Error"));
      };

    const fetchPassengers = async () => {
        await getPassengers().then((data) => setPassengers(data));
      };
      
      const fetchFlights = async () => {
        await getFlights().then((data) => setFlights(data));
      };
    
      useEffect(() => {
        fetchPassengers();
        fetchFlights();
      }, []);
    
      const selectPassengers = useMemo<ISelect[]>(() => {
        return [
          { value: "0", label: "Select item..." },
          ...passengers.map((passenger) => {
            return {
              value: passenger.passengerId.toString(),
              label: `Id: ${passenger.passengerId}, First and last name: ${passenger.lastName} ${passenger.firstName}`,
            };
          }),
        ];
      }, [passengers]);

      const selectFlights = useMemo<ISelect[]>(() => {
        return [
          { value: "0", label: "Select item..." },
          ...flights.map((flight) => {
            return {
              value: flight.flightId.toString(),
              label: `Id: ${flight.flightId}, Number: ${flight.flightNumber}`,
            };
          }),
        ];
      }, [flights]);

    return (
        <Modal show={show} onHide={onHide}>
            <form onSubmit={handleSubmit(onSubmit)}>
              <Modal.Header closeButton>
                <Modal.Title>Modal title</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <div
                  asp-validation-summary="ModelOnly"
                  className="text-danger"
                ></div>
                <div className="form-group">
                  <label className="control-label">Weight</label>
                  <Controller
                    control={control}
                    name={"weight"}
                    rules={{
                      min: {
                        value: 0,
                        message: "Minimum 0"
                      },
                      required: "enter weight",
                    }}
                    render={({ field }) => (
                      <input type="number" className="form-control" {...field} />
                    )}
                  ></Controller>
                  <p style={{ color: "red" }}>{errors.weight?.message}</p>
                </div>
                <div className="form-group">
                  <label className="control-label">Width</label>
                  <Controller
                    control={control}
                    name={"width"}
                    rules={{
                      min: {
                        value: 0,
                        message: "Minimum 0"
                      },
                      required: "enter width",
                    }}
                    render={({ field }) => (
                      <input type="number" className="form-control" {...field} />
                    )}
                  ></Controller>
                  <p style={{ color: "red" }}>{errors.width?.message}</p>
                </div>
                <div className="form-group">
                  <label className="control-label">Height</label>
                  <Controller
                    control={control}
                    name={"height"}
                    rules={{
                      min: {
                        value: 0,
                        message: "Minimum 0"
                      },
                      required: "enter height",
                    }}
                    render={({ field }) => (
                      <input type="number" className="form-control" {...field} />
                    )}
                  ></Controller>
                  <p style={{ color: "red" }}>{errors.height?.message}</p>
                </div>
                <div className="form-group">
                  <label className="control-label">Depth</label>
                  <Controller
                    control={control}
                    name={"depth"}
                    rules={{
                      min: {
                        value: 0,
                        message: "Minimum 0"
                      },
                      required: "enter depth",
                    }}
                    render={({ field }) => (
                      <input type="number" className="form-control" {...field} />
                    )}
                  ></Controller>
                  <p style={{ color: "red" }}>{errors.depth?.message}</p>
                </div>
                <div className="form-group">
                  <label className="control-label">Description</label>
                  <Controller
                    control={control}
                    name={"description"}
                    rules={{
                      required: "enter description",
                    }}
                    render={({ field }) => (
                      <input className="form-control" {...field} />
                    )}
                  ></Controller>
                  <p style={{ color: "red" }}>{errors.description?.message}</p>
                </div>
                <div className="form-group">
                <label className="control-label">Passenger</label>
                <Controller
                  control={control}
                  name={"passengerId"}
                  rules={{
                    required: "enter passenger",
                    validate: (data) => (data != 0 ? undefined : "Select passenger"),
                  }}
                  render={({ field }) => (
                    <select className="form-control" {...field}>
                      {selectPassengers.map(({ value, label }) => (
                        <option key={value} value={value}>
                          {label}
                        </option>
                      ))}
                    </select>
                  )}
                ></Controller>
                <p style={{ color: "red" }}>{errors.passengerId?.message}</p>
              </div>
                <div className="form-group">
                <label className="control-label">Flight</label>
                <Controller
                  control={control}
                  name={"flightId"}
                  rules={{
                    required: "enter flight",
                    validate: (data) => (data != 0 ? undefined : "Select flight"),
                  }}
                  render={({ field }) => (
                    <select className="form-control" {...field}>
                      {selectFlights.map(({ value, label }) => (
                        <option key={value} value={value}>
                          {label}
                        </option>
                      ))}
                    </select>
                  )}
                ></Controller>
                <p style={{ color: "red" }}>{errors.flightId?.message}</p>
              </div>
              </Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={onHide}>
                  Close
                </Button>
                <Button variant="primary" type="submit">
                  Save
                </Button>
              </Modal.Footer>
            </form>
          </Modal>
      )
}
